  `spid` int(20) NOT NULL AUTO_INCREMENT,
  `project_name` varchar(200) DEFAULT NULL COMMENT '示范项目名称',
  `project_info` text COMMENT '示范项目简介',
  `rsd_id` int(20) DEFAULT NULL COMMENT '所属研究方向id',
  PRIMARY KEY (`spid`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

