INSERT INTO `team` VALUES ('1', '大数据、云计算及智能技术团队', '<p>“大数据、云计算及智能技术方向”有教师15人（含教授5人、副教授4人、讲师6人）、博士研究生26人（含留学生11人）、硕士研究生80余人（含留学生3人）。<br></p>'), ('3', '机器人与人工智能团队', '<p>“机器人、智能系统与先进控制理论方向”有教师11人（含教授4人、副教授2人、讲师5人）、博士研究生5人、硕士研究生36人。<br></p>'), ('4', '地理信息系统、虚拟地理环境领域团队', '<p>地理信息技术与虚拟地理环境方向”有教师14人（含教授5人、副教授7人、讲师2人）、博士研究生10余人，硕士研究生40余人。<br></p>'), ('5', '政策、制度、法律法规领域团队', '<p>&nbsp;<span style=\"color: inherit;\">“政策、制度、法律、法规方向”有教师4人（含教授1人、副教授1人、讲师2人）、博士研究生2人、硕士研究生10余人。</span></p>');
